// import MetricsGraphics from 'react-metrics-graphics';
// import 'metrics-graphics/dist/metricsgraphics.css';
//
import axios from 'axios';
import Card from "@material-ui/core/Card";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import DashboardIcon from "@material-ui/icons/Dashboard";
import SettingsIcon from "@material-ui/icons/Settings";
import DeleteIcon from "@material-ui/icons/Delete";
import Typography from "@material-ui/core/Typography";
import CardContent from "@material-ui/core/CardContent";
import React, {useEffect, useState} from "react";
import {Image, Modal} from "react-bootstrap";
import {makeStyles} from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import {Button, Form, FormGroup, Input} from "reactstrap";

const useStyles = makeStyles(theme => ({
    exchangeHeader: {
        backgroundColor: "#A0A0A0",
        color: "#FFFFFF"
    },
    exchangeConfig: {
        align: "right",
    },
}));

const getLatestExchangeData = async (base="EUR") => {
    try {
        const response = await axios({
            method: 'GET',
            url: 'https://api.exchangeratesapi.io/latest?base=' + base,
        });
        return response.data;
    } catch (e) {
        console.log("error: ", e);
    }
};

const getExchangeDataStartEnd = async (base="EUR", start="2018-01-01", end="2019-01-01") => {
    try {
        const response = await axios({
            method: 'GET',
            url: 'https://api.exchangeratesapi.io/history?base=' + base + '&start_at=' + start + '&end_at=' + end,
        });
        return response.data;
    } catch (e) {
        console.log("error: ", e);
    }
};

const ExchangeCard = (props) => {
    const classes = useStyles();
    const [data, setData] = useState(undefined);
    const [Param, setParam] = useState(props.param);
    const [Base, setBase] = useState("EUR");
    const [Currency, setCurrency] = useState("USD");
    const [modalParam, setModalParam] = useState("last");
    const [modalRover, setModalRover] = useState("Opportunity");

    useEffect(
        () => { async function fetchData() {
            let res = await getLatestExchangeData(Base);
            setData(res);
        }
            fetchData();
        }, [Base, Currency]);

    const content = (data) => {
        // return (
        //     <MetricsGraphics
        //         title="Downloads"
        //         description="This graphic shows a time-series of downloads."
        //         data={ [{'date':new Date('2014-11-01'),'value':12}, {'date':new Date('2014-11-02'),'value':18}] }
        //         width={600}
        //         height={250}
        //         x_accessor="date"
        //         y_accessor="value"
        //     />
        // );
        if (data === undefined) {
            return (
                <div style={{textAlign: "center", marginLeft: "15px", marginRight: "15px", marginTop: "15px"}}>
                    <h4>Loading</h4>
                </div>
            );
        } else {
            let base = data.base;
            let date = data.date;
            let currencies = data.rates;
            return (
                <div>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <Typography variant="body1" color="textSecondary">
                                Date : {date}
                            </Typography>
                        </Grid>
                        <Grid item xs={6}>
                            <Typography variant="h6">
                                Base Currencies :
                            </Typography>
                            <Typography variant="body1" color="textSecondary">
                                {base}
                            </Typography>
                        </Grid>
                        <Grid item xs={6}>
                            <Typography variant="h6">
                                Exchange Currencies :
                            </Typography>
                            <Typography variant="body1" color="textSecondary">
                                {currencies}
                            </Typography>
                        </Grid>
                    </Grid>
                </div>
            );
        }
    };

    const [modal, setModal] = useState(true);

    const toggle = () => setModal(!modal);

    return (
        <Card>


            {/*<Modal show={modal} onHide={toggle} centered>*/}
            {/*    <Modal.Header closeButton>*/}
            {/*        <Modal.Title>Params</Modal.Title>*/}
            {/*    </Modal.Header>*/}
            {/*    <Modal.Body>*/}
            {/*        <Form>*/}
            {/*            <FormGroup>*/}
            {/*                <label>Rover</label>*/}
            {/*                <Input type="select" onChange={e => setModalRover(e.target.value)}>*/}
            {/*                    <option selected={Rover === "Opportunity"}>Opportunity</option>*/}
            {/*                    <option selected={Rover === "Curiosity"}>Curiosity</option>*/}
            {/*                    <option selected={Rover === "Spirit"}>Spirit</option>*/}
            {/*                </Input>*/}
            {/*            </FormGroup>*/}
            {/*        </Form>*/}
            {/*    </Modal.Body>*/}
            {/*    <Modal.Body>*/}
            {/*        <Form>*/}
            {/*            <FormGroup>*/}
            {/*                <label>Param</label>*/}
            {/*                <Input type="select" onChange={e => setModalParam(e.target.value)}>*/}
            {/*                    <option selected={Param === "last"}>last</option>*/}
            {/*                    <option selected={Param === "random"}>random</option>*/}
            {/*                </Input>*/}
            {/*            </FormGroup>*/}
            {/*        </Form>*/}
            {/*    </Modal.Body>*/}
            {/*    <Modal.Footer>*/}
            {/*        <Button className={classes.save} onClick={() => {setBase(modalRover); setParam(modalParam); toggle()}}>Save</Button>*/}
            {/*    </Modal.Footer>*/}
            {/*</Modal>*/}


            <Toolbar position="static" className={classes.exchangeHeader}>
                <Grid container spacing={1}>
                    <Grid item xs={2}>
                        <IconButton edge="start" color="inherit" aria-label="menu">
                            <DashboardIcon/>
                        </IconButton>
                    </Grid>
                    <Grid item xs={8}>
                        <div style={{marginTop: "10px"}}>
                            <Typography style={{textAlign: "center"}} variant="h5">
                                Exchange
                            </Typography>
                        </div>
                    </Grid>
                    <Grid item xs={1}>
                        <IconButton color="inherit" aria-label="menu"  onClick={toggle}>
                            <SettingsIcon/>
                        </IconButton>
                    </Grid>
                    <Grid item xs={1}>
                        <IconButton color="inherit" aria-label="menu">
                            <DeleteIcon/>
                        </IconButton>
                    </Grid>
                </Grid>
            </Toolbar>
            <CardContent>
                {content(data)}
            </CardContent>
        </Card>
    );
};

export default ExchangeCard;
