import axios from 'axios';
import Card from "@material-ui/core/Card";
import Grid from "@material-ui/core/Grid";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import DashboardIcon from "@material-ui/icons/Dashboard";
import SettingsIcon from "@material-ui/icons/Settings";
import Typography from "@material-ui/core/Typography";
import CardContent from "@material-ui/core/CardContent";
import React, {useEffect, useState} from "react";
import {makeStyles} from "@material-ui/core";

const apiKey = "3cLQtZD1ekZ0pnWKYCZ5JMGqrp3BGAVQ";
const entryPoint = "http://dataservice.accuweather.com/";

const useStyles = makeStyles(theme => ({
    nasaHeader: {
        backgroundColor: "#FFA500",
        color: "#FFFFFF"
    },
}));

const getLocationKey = async (city) => {
    try {
        const response =  await axios ({
            method: 'GET',
            url: entryPoint + 'locations/v1/cities/search?q=' + city + '&apikey=' + apiKey,
        })
        return response.data;
    }
    catch (e) {
        console.log("error: ", e);
    }
};

const getInfoFromLocation = async (locationKey) => {
    try {
        const response = await axios({
            method: 'GET',
            url: entryPoint + 'forecasts/v1/daily/1day/' + locationKey + '?apikey=' + apiKey,
        });
        return response.data;
    } catch (e) {
        console.log("error: ", e);
    }
};

const AccuWeatherCard = (props) => {
    const classes = useStyles();
    const [data, setData] = useState(undefined);

    useEffect(
        () => { async function fetchData() {
            let location = await getLocationKey(props.city);
            let res = await getInfoFromLocation(location[0].Key);
            setData(res);
        }
        fetchData();
    }, []);

    const content = (data) => {
        if (data === undefined) {
            return (
                <div style={{textAlign: "center", marginLeft: "15px", marginRight: "15px", marginTop: "15px"}}>
                    <h4>Loading</h4>
                </div>
            );
        } else {
            let date = data.DailyForecasts[0].Date;
            let maxTemp = (parseInt(data.DailyForecasts[0].Temperature.Maximum.Value, 10) - 32) * (5/9);
            let minTemp = (parseInt(data.DailyForecasts[0].Temperature.Minimum.Value, 10) - 32) * (5/9);
            let dailyIcon = data.DailyForecasts[0].Day.Icon;
            let dailyIconPhrase = data.DailyForecasts[0].Day.IconPhrase;
            let nightlyIcon = data.DailyForecasts[0].Night.Icon;
            let nightlyIconPhrase = data.DailyForecasts[0].Night.IconPhrase;

            return (
                <div>
                    <Typography gutterBottom variant="h6">
                        {props.city}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                        {date}<br/>
                    </Typography>
                    <Grid container spacing={2}>
                        <Grid item xs={12} style={{textAlign: "center"}}>
                            maxTemp {maxTemp} °C<br/>
                            minTemp {minTemp} °C<br/>
                        </Grid>
                        <Grid item xs={6}>
                            <Typography variant="body2" color="textSecondary">
                                dailyIcon {dailyIcon}<br/>
                                dailyIconPhrase {dailyIconPhrase}<br/>
                            </Typography>
                        </Grid>
                        <Grid item xs={6}>
                            <Typography variant="body2" color="textSecondary">
                                nightlyIcon {nightlyIcon}<br/>
                                nightlyIconPhrase {nightlyIconPhrase}
                            </Typography>
                        </Grid>
                    </Grid>
                </div>
            );
        }
    };

    return (
        <Card>
            <Toolbar position="static" className={classes.nasaHeader}>
                <IconButton edge="start" color="inherit" aria-label="menu">
                    <DashboardIcon/>
                </IconButton>
                <Typography variant="h5">
                    AccuWeather
                </Typography>
                <IconButton color="inherit" aria-label="menu">
                    <SettingsIcon/>
                </IconButton>
            </Toolbar>
            <CardContent>
                {content(data)}
            </CardContent>
        </Card>
    );
};

export default AccuWeatherCard;
