import axios from 'axios';
import Card from "@material-ui/core/Card";
import Grid from "@material-ui/core/Grid";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import DashboardIcon from "@material-ui/icons/Dashboard";
import SettingsIcon from "@material-ui/icons/Settings";
import DeleteIcon from "@material-ui/icons/Delete";
import Typography from "@material-ui/core/Typography";
import CardContent from "@material-ui/core/CardContent";
import React, {useEffect, useState} from "react";
import { Modal } from 'react-bootstrap';
import { Button, Form, FormGroup, Input} from 'reactstrap';
import {makeStyles} from "@material-ui/core";
import {Image} from "react-bootstrap";

const entryPoint = "https://opentdb.com/api.php?";

const useStyles = makeStyles(theme => ({
    nasaHeader: {
        backgroundColor: "#c51ec5",
        color: "#FFFFFF"
    },
    save: {
        backgroundColor: "#12ACDE",
        border: "#12ACDE",
        color: "#FFFFFF",
        "&:hover": {
            backgroundColor: "#0F91BC"
        }
    },
}));

const getQuestion = async (difficulty="", category="") => {
    try {
        const response =  await axios ({
            method: 'GET',
            url: entryPoint + 'amount=1&difficulty=' + difficulty + '&category=' + category,
        })
        return response.data;
    }
    catch (e) {
        console.log("error: ", e);
    }
};

const QuizzCard = (props) => {
    const classes = useStyles();
    const [data, setData] = useState(undefined);
    const [difficulty, setDifficulty] = useState("");
    const [category, setCategory] = useState("");

    useEffect(
        () => { async function fetchData() {
            let res = await getQuestion(difficulty, category);
            setData(res);
        }
            fetchData();
        }, [difficulty, category]);

    const content = (data) => {
        if (data === undefined) {
            return (
                <div style={{textAlign: "center", marginLeft: "15px", marginRight: "15px", marginTop: "15px"}}>
                    <h4>Loading</h4>
                </div>
            );
        } else {
            let theme = data.results[0].category;
            return (
                <div>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <Typography variant="h6">
                                {name}
                            </Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography variant="body1" color="textSecondary">
                                Species : {species}
                            </Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography variant="body1" color="textSecondary">
                                Blood Status : {bloodStatus}
                            </Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography variant="body1" color="textSecondary">
                                School : {school}
                            </Typography>
                        </Grid>
                        <Grid item xs={12}>
                            <Typography variant="body1" color="textSecondary">
                                House : {house}
                            </Typography>
                        </Grid>
                    </Grid>
                </div>
            );
        }
    };

    const [modal, setModal] = useState(false);
    const [modalDifficulty, setModalDifficulty] = useState("medium");

    const toggle = () => setModal(!modal);

    return (
        <Card>
            <Modal show={modal} onHide={toggle} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Choose a house</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <FormGroup>
                            <label>Houses</label>
                            <Input type="select" onChange={e => setModalDifficulty(e.target.value)}>
                                <option selected={difficulty === "easy"}>Easy</option>
                                <option selected={difficulty === "medium"}>Medium</option>
                                <option selected={difficulty === "hard"}>Hard</option>
                            </Input>
                        </FormGroup>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button className={classes.save} onClick={() => {setDifficulty(modalDifficulty); toggle()}}>Save</Button>
                </Modal.Footer>
            </Modal>
            <Toolbar position="static" className={classes.nasaHeader}>
                <Grid container spacing={1}>
                    <Grid item xs={2}>
                        <IconButton edge="start" color="inherit" aria-label="menu">
                            <DashboardIcon/>
                        </IconButton>
                    </Grid>
                    <Grid item xs={8}>
                        <div style={{marginTop: "10px"}}>
                            <Typography style={{textAlign: "center"}} variant="h5">
                                Quizz
                            </Typography>
                        </div>
                    </Grid>
                    <Grid item xs={1}>
                        <IconButton color="inherit" aria-label="menu" onClick={toggle}>
                            <SettingsIcon/>
                        </IconButton>
                    </Grid>
                    <Grid item xs={1}>
                        <IconButton color="inherit" aria-label="menu">
                            <DeleteIcon/>
                        </IconButton>
                    </Grid>
                </Grid>
            </Toolbar>
            <CardContent>
                {content(data)}
            </CardContent>
        </Card>
    );
};

export default QuizzCard;
