import axios from 'axios';
import Card from "@material-ui/core/Card";
import AppBar from "@material-ui/core/AppBar/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import DashboardIcon from "@material-ui/core/SvgIcon/SvgIcon";
import Typography from "@material-ui/core/Typography";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import React, {useEffect, useState} from "react";
import {Image} from "react-bootstrap";

const getNasaDailyImg = async () => {
    try {
        const response =  await axios ({
            method: 'GET',
            url: 'https://api.nasa.gov/planetary/apod?api_key=pANfIB5rv8J2nbFIbpNfua8WgGYtpQ8BEa8fsZXk',
        })
        return response.data;
    }
    catch (e) {
        console.log("error: ", e);
    }
};

const getMarsImgFromRover = async (rover, sol) => {
    try {
        const response = await axios({
            method: 'GET',
            url: 'https://api.nasa.gov/mars-photos/api/v1/rovers/' + rover + '/photos?sol=' + sol + '&api_key=pANfIB5rv8J2nbFIbpNfua8WgGYtpQ8BEa8fsZXk',
        });
        return response.data;
    } catch (e) {
        console.log("error: ", e);
    }
};

const NasaCard = (props) => {
    const [data, setData] = useState(undefined);
    const [sol, setSol] = useState(undefined);

    useEffect( async() => {
        let tmp = await getMarsImgFromRover(props.rover, 1);
        let sol;
        if (props.param === "random")
            sol = Math.floor(Math.random() * tmp.photos[0].rover.max_sol) + 1;
        else
            sol = tmp.photos[0].rover.max_sol;
        let res = await getMarsImgFromRover(props.rover, sol);
        setData(res);
        setSol(sol);
    }, []);

    const content = (data) => {
        if (data === undefined) {
            return <h4>Loading</h4>;
        } else {
            if (data.photos[0] !== undefined) {
                return <div>
                        <Image src={data.photos[0].img_src} fluid/>
                        <h4>Picture were taken on {data.photos[0].earth_date} (sol : {sol}) by the {data.photos[0].camera.full_name}</h4>
                       </div>;
            } else {
                return <h4>No picture were taken on sol {sol}</h4>;
            }
        }
    };

    return (
        <Card>
            <AppBar position="static">
                <Toolbar>
                    <IconButton edge="start" color="inherit" aria-label="menu">
                        <DashboardIcon/>
                    </IconButton>
                    <Typography variant="h6">
                        Nasa {props.rover} camera
                    </Typography>
                </Toolbar>
            </AppBar>
            <CardContent>
                <div style={{textAlign: "center", marginLeft: "15px", marginRight: "15px", marginTop: "15px"}}>
                    {content(data)}
                </div>
            </CardContent>
        </Card>
    );
};

export default NasaCard;
